import { useState, ChangeEvent, useEffect } from 'react'
import Search from './components/Search'
import { optionType } from './types'

const App = (): JSX.Element => {
  const [search, setSearch] = useState<string>('')
  const [options, setOptions] = useState<[]>([])
  const [city, setCity] = useState<optionType | null>()
  const [forecast, setForecast] = useState<null>(null)
  const getOptions = (value: string) => {
    fetch(
      `http://api.openweathermap.org/geo/1.0/direct?q=${value}&limit=5&appid=${process.env.REACT_APP_API_KEY}`
    )
      .then((response) => response.json())
      .then((data) => {
        setOptions(data)
      })
      .catch((err) => console.log(err + 'err'))
  }
  const getForecast = (option: optionType) => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${option.lat}&lon=${option.lon}&exclude={part}&appid=${process.env.REACT_APP_API_KEY}`
    )
      .then((res) => res.json())
      .then((data) => setForecast(data))
  }

  const searchHandler = (e: ChangeEvent<HTMLInputElement>) => {
    setSearch(e.target.value)
    if (e.target.value === '') return
    getOptions(e.target.value)
  }
  const setOptionsData = (option: optionType) => {
    setCity(option)
  }
  const onSubmit = () => {
    if (!city) return
    getForecast(city)
  }
  useEffect(() => {
    if (city) {
      setSearch(city.name)

      setOptions([])
    }
  }, [city])
  return (
    <main className="flex justify-center items-center bg-gradient-to-br from-sky-400 via-rose-400 to-lime-400 h-[100vh] w-full">
      {forecast ? (
    <h1></h1>
      ) : (
        <Search
          search={search}
          searchHandler={searchHandler}
          options={options}
          onSubmit={onSubmit}
          setOptionsData={setOptionsData}
        />
      )}
    </main>
  )
}

export default App
